INSERT INTO `wp_options` VALUES("142", "theme_mods_shapely", "a:16:{i:0;b:0;s:18:\"nav_menu_locations\";a:1:{s:7:\"primary\";i:0;}s:18:\"custom_css_post_id\";i:21;s:12:\"header_image\";s:73:\"http://localhost/mysite/wp-content/uploads/2019/06/cropped-RockyBeach.jpg\";s:17:\"header_image_data\";O:8:\"stdClass\":5:{s:13:\"attachment_id\";i:6;s:3:\"url\";s:73:\"http://localhost/mysite/wp-content/uploads/2019/06/cropped-RockyBeach.jpg\";s:13:\"thumbnail_url\";s:73:\"http://localhost/mysite/wp-content/uploads/2019/06/cropped-RockyBeach.jpg\";s:6:\"height\";i:225;s:5:\"width\";i:1900;}s:16:\"background_image\";s:0:\"\";s:21:\"background_position_x\";s:6:\"center\";s:21:\"background_position_y\";s:6:\"center\";s:9:\"blog_name\";s:1:\" \";s:26:\"shapely_transparent_header\";b:0;s:34:\"shapely_sticky_header_transparency\";i:60;s:21:\"shapely_sticky_header\";b:0;s:11:\"custom_logo\";i:18;s:22:\"shapely_logo_dimension\";a:2:{s:5:\"width\";d:650;s:6:\"height\";d:173;}s:16:\"header_textcolor\";s:5:\"blank\";s:22:\"mobile_menu_on_desktop\";b:0;}", "yes");
